/*发表评论按钮动画*/
.comment-submit {
    transition: all 0.3s !important;
    overflow: hidden;
}
.comment-submit:hover {
    animation: words_in_out 0.5s;
}
.comment-submit:hover:after {
    animation: in_out 0.5s;
}
.comment-submit:after {
    font-family: 'Material Icons';
    content: "\e163";
    font-size: 35px;
    position: absolute;
    left: -57px;
    top: 0px;
    color: #FFF;
    line-height: 64px;
    transition: all 0.3s;
}
@keyframes in_out {
    0% {
        left: -47px;
    }
    40% {
        left: 13px;
    }
    60% {
        left: 21px;
    }
    100% {
        left: 67px;
    }
}
@keyframes words_in_out {
    0% {
        color: #FFF;
    }
    50% {
        color: rgb(0, 181, 229);
    }
    100% {
        color: #FFF;
    }
}

