sourceCode:
/* ==UserStyle==
@name           谷歌背景替换
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    只是替换了背景，并且去除了一些小细节
@author         Me
==/UserStyle== */
@-moz-document regexp("^https?://www.google.[a-z.]{2,6}(/?|/\\?(gfe|pli|hl).*)"), regexp("^https?://www.google.[a-z.]{2,6}/(search|webhp|imghp)(.(?!(&tbm=lcl)))*") {
	/*滚动条*/
	::-webkit-scrollbar{
		width: 6px;
		height:6px;
		background-color: #66ccff3d;
	}
	::-webkit-scrollbar-track{
		-webkit-box-shadow: 0 0 3px rgba(0,0,0,0.3) inset;
		background-color: #c6dfbc3d;
	}
	::-webkit-scrollbar-thumb{
		border-radius: 3px;
		-webkit-box-shadow: 0 0 3px rgba(0,0,0,0.3) inset;
		background-color: #999d;
	}
	::-webkit-scrollbar-thumb:hover{
		background-color: #777d;
	}
	::-webkit-scrollbar-thumb:active{
		background-color: #555d;
	}
	html{
		/*背景图片*/
		--background-img-src:url(//miao.su/images/2019/09/05/73f01436a180c42ca5407.jpg);
	}
	body::before{
		content:"";
		position:fixed;
		width:100%;
		height:100vh;
		background-image:var(--background-img-src);          /*设置固定背景*/
		background-size:cover;
		z-index:-100;
	}
	.sfbg{
		background-color:transparent;/*搜索框背景透明*/
	}
/* 	拍照或语音搜索hover提示框 */
	body > div[style*="background: rgb(45, 45, 45); border: 1px solid rgb(255, 255, 255); color: rgb(255, 255, 255); display: block;"]{
		border:none!important;
		border-radius:5px;
	}
	body > div[style*="background: rgb(45, 45, 45); border: 1px solid rgb(255, 255, 255); color: rgb(255, 255, 255); display: block;"] > div{
		border-color:transparent!important;
	}
	[jsname="RNNXgb"]{            /*搜索框背景色*/
		background-color:#ffffff75;
	}
	[jsname="RNNXgb"]:hover{            /*搜索框输入背景色*/
		background-color:#ffffffc7;
	}
	.emcav [jsname="RNNXgb"],
	.A9xB4 [jsname="RNNXgb"]{						/*搜索框联想搜索出现时背景*/
		background-color:#ffffffc7;
	}
	.aajZCb,.lV0vqc{            /*搜索框输入背景色*/
		background-color:#ffffffc7;
	}
	.sbhl,.Vwmzxb{					/*搜索记录hover背景*/
		background: rgba(0,0,0,.2);
	}
	#fbar{       /* 底部样式     */
		background-color:rgba(242, 242, 242, 0.25);
		border-color:transparent;                             /*去除白色细线*/
	}
	#fbar .b2hzT{			/*当出现地区时，去除内部白色细线*/
		border-color:transparent; 
	}
}
@-moz-document regexp("^https?://www.google.[a-z.]{2,6}/(search|webhp|imghp)(.(?!(&tbm=lcl)))*") {
	html{
		/*统一盒子背景颜色*/
		--box-background-color:#66CCFF3D;
		/*侧边栏隐藏背景*/
		--sidebar-color-hidden:#66ffd470;
		/*侧边栏显示背景*/
		--sidebar-color-show:#7ed4ffc9;
		/*视频选项hover前背景*/
		--videobackground-color:#ffffff6b;
		/*视频选项hover后背景*/
		--videobackground-color-hover:#ffffffd1;
	}
	body{
		width:100%;
	}
	body:before{
		opacity:0.8;			/*背景图片透明度*/
	}
	.minidiv .sfbg{         /* 吸顶搜索框 */
		background-color:#ffffff24;
	}
	#lb > div{				/*设置、更多选项背景*/
		background-color:#ffffffc7;
	}
	#hdtb{                  /*搜索结果设置栏下划线*/
		border-bottom-color:transparent;
		background-color:transparent;
	}
	#hdtbSum,.jZWadf,.qcTKEe{               /*搜索结果设置栏*/
		background: #fff0;
	}
	.ymoOte .Ew9oWb{
		border-top-color:transparent;
	}
	.cv2VAd{
		border-color:transparent;           /*视频选项边框无色*/
	}
	g-section-with-header{
		width:95%;
	}
	g-inner-card {
		background-color: var(--videobackground-color);              /*视频栏背景*/
		transition-duration: 0.5s;
	}
	g-inner-card:hover {              /*视频栏hover背景*/
		background-color: var(--videobackground-color-hover);
	}
	.hdtb-td-c[aria-expanded=false],.ECgenc.eLNT1d{			/*更多选项工具，未激活不显示*/
		opacity:0;
	}
	.aR1mEe{					/*相关搜索下划线透明*/
		border-bottom-color:transparent;
	}
/* 	布局升级，不仅仅替换背景 */
	#search{
		counter-reset:counters;		/*计数器*/
		--search-box-width:calc(50vw - 132px);	/*每个盒子的宽度*/
	}
	@media screen and (min-width: 1650px){
		#search{
			--search-box-width:calc(33vw - 132px);
		}
	}
	@media screen and (max-width: 765px){
		#search{
			--search-box-width:100%;
		}
	}
	#rso{
		width:calc(100vw - 185px)!important;
	}
	.srg{				/*网络搜索结果，正常结果，不包含高亮*/
		width:calc(100vw - 185px)!important;
	}
	.srg .g,#rso > .g{			/*每个搜索结果框*/
		position:relative;
		max-width:var(--search-box-width);
		min-width:var(--search-box-width);
		border: solid 1px rgba(0,0,0,0.13);
		border-radius: 8px;
		margin:10px;
		padding:5px;
		min-height:100px;
		background-color:var(--box-background-color);			/*添加背景以防看不清*/
		box-shadow: 0 0 30px #eee1 inset,2px 2px 7px rgba(130,170,190,.8);
		vertical-align:top;
	}
	.srg .g:before,#rso > .g:before{			/*计数器*/
		content:counter(counters)".";
		display:block;
		position:absolute;
		left:-18px;
		font-family: arial, sans-serif;
		counter-increment:counters;
	}
	.srg .exp-outline{		/*隐藏边框*/
		display:none;
	}
	.qmv19b{				/*焦点新闻部分*/
		border: solid 1px rgba(0,0,0,0.13);
		background-color: var(--box-background-color);
		box-shadow: 0 0 30px #eee1 inset, 2px 2px 7px rgba(130,170,190,.8)，;
	}
	.AUiS2{				/*其他人还搜了部分整体盒子宽度调整*/
		margin-left: 0;
    	margin-right: 0;
	}
	.AUiS2 .XCKyNd{				/*其他人还搜了部分叉位置调整*/
		right:0;
	}
	.ellip{					/*搜索结果标题宽度，撑开父级大小*/
/* 		max-width:var(--search-box-width); */
	}
/* 	特殊结果部分 */
	.bkWMgd:last-child:not(:first-child),.bkWMgd:not(:last-child){			/*特殊样式部分正常搜索宽度   特殊样式部分外部盒子*/
/* 		--search-box-width:calc(100vw - 300px); */
	}
	.bkWMgd > div[data-hveid] ~ .g{	/*谷歌学术部分*/
		display:inline-block;
		position:relative;
		max-width:var(--search-box-width);
		min-width:var(--search-box-width);
		border: solid 1px rgba(0,0,0,0.13);
		border-radius: 8px;
		padding:5px;
		min-height:100px;
		background-color:var(--box-background-color);			/*添加背景以防看不清*/
		box-shadow: 0 0 30px #eee1 inset,2px 2px 7px rgba(130,170,190,.8);
		vertical-align:top;
		margin-right:27px;
	}
	.bkWMgd > .g{			/*特殊搜索结果盒子宽度*/
		width:var(--search-box-width);
		position:relative;
	}
	.bkWMgd > .g .exp-outline{		/*去除已浏览过的外边框*/
		display:none;
	}
	.bkWMgd > .g:before{			/*计数器*/
		content:counter(counters)".";
		display:block;
		position:absolute;
		left:-18px;
		font-family: arial, sans-serif;
		counter-increment:counters;
	}
	.bkWMgd > .bNg8Rb + div[class~="g"] .rc,.bkWMgd > .g > .bNg8Rb + div{				/*另一种特殊搜索结果的样式*/
		padding:5px;
		background-color: var(--box-background-color);
		box-shadow: 0 0 30px #eee1 inset, 2px 2px 7px rgba(130,170,190,.8);
		border: solid 1px rgba(0,0,0,0.13);
		border-radius: 8px;
		width:max-content;
		transition:none!important;
	}
	#center_col .kp-blk,#center_col .kno-ftr{/*百科宽度同意*/
		margin-left: 0;
    	margin-right: 0;
		padding:0;
		border-color:transparent;
		max-width:var(--search-box-width);
		min-width:var(--search-box-width);
	}
	#appbar{		/*流派音乐结果切换列表*/
		background-color:transparent;
	}
	#appbar div.rl_feature,
	#appbar .rlc__slider,
	#appbar .rlc__slider-page{		/*音乐切换列表内部背景透明*/
		background-color:transparent;
	}
	#appbar .rl_center{
		border:none;
	}
	.lr_c_tf{		/*电影预定时间栏*/
		background-color:var(--box-background-color);
	}
	.pVFdhc{			/*电影预定分割线*/
		background-color:rgba(0,0,0,0.13);
	}
	.vk_arc{			/*电影预定footer分割线*/
		border-top-color:rgba(0,0,0,0.13);
	}
	.tb_l{				/*电影预定日期分割线*/
		border-left-color:transparent;
	}
	.abhAW{				/*比赛日程表分割线*/
		border-bottom-color:rgba(0,0,0,0.13);
	}
	.liveresults-sports-immersive__match-grid-right-border{		/*比赛日程表每场右分割线颜色*/
		border-right-color:rgba(0,0,0,0.13);
	}
	.liveresults-sports-immersive__match-grid-bottom-border{	/*比赛日程表每行下分割线颜色*/
		border-bottom-color:rgba(0,0,0,0.13);
	}
	.imspo_mt__sc-spc{		/*比赛日程表时间与队伍分割中竖线颜色*/
		background-color:rgba(0,0,0,0.13);
	}
	.sBROid{				/*比赛日程表footer分割线*/
		border-top-color:rgba(0,0,0,0.13);
	}
	.imso-hov:hover, .imso-hov:focus{			/*hover背景色*/
		background-color:var(--box-background-color);
	}
	.cbphWd{				/*其他用户还问了以下问题选项边框同步父级*/
		border-top-color:rgba(0,0,0,0.13);
	}
	.g .rc [data-base-uri] > div[jsname]{/*其他用户还搜了*/
		background-color:var(--box-background-color);
		border-color:transparent;
		box-shadow: 0 0 30px #eee1 inset,0 0 7px rgba(130,170,190,.8);
	}
	#extrares{				/*相关扩展宽度同父级，取消padding导致的半个图标隐藏*/
		width:100%;
	}
	.EPLo7b{			/*热门故事分割线*/
		border-top-color:rgba(0,0,0,0.13);
	}
/* 	侧滑栏 */
	.mw{			/*父级宽度撑开100%*/
		max-width:unset!important;
	}
	#rcnt{		/*宽度同父级，内部侧滑栏超出内容隐藏*/
		width:100%;
		overflow:hidden;
	}
	#rhs{			/*侧滑栏*/
		float:right;
		margin-right: -485px;
		transition-duration:.5s;
		width: 540px;
	}
	#rhs:hover{				/*侧滑栏hover*/
		margin-right:0;
	}
	#rhs .rhsvw > .kp-blk,#rhs [class*=panel]{				/*侧滑栏隐藏背景*/
		border-radius:8px;
		background-color:var(--sidebar-color-hidden);
		border-color:rgba(0,0,0,0.13);
		box-shadow: 0 0 30px #eee1 inset,2px 2px 7px rgba(130,170,190,.8);
	}
	#rhs:hover .rhsvw > .kp-blk,#rhs:hover [class*=panel]{			/*侧滑栏显示背景*/
		background-color:var(--sidebar-color-show);
	}
	.kp-blk .xpdclose .farUxc, .kp-blk .xpdopen .mJ2Mod,.xpdclose .a84NUc:not(:empty), .xpdopen .CQKTwc:not(:empty),.wlqJjf.gws-local-riddler__banner-shown,.XQvBOc{		/*侧滑栏内部分割线*/
		border-color:rgba(0,0,0,0.13);
	}
	#rhs .y8Jpof{			/*侧边栏联想结果过渡*/
		transition-duration:.5s;
	}
	#rhs .y8Jpof:hover{					/*侧边栏联想结果hover背景*/
		background-color:rgba(43, 135, 240, .55);
	}
	#rhs .TieM1d{			/*特殊侧滑栏圆角*/
		border-radius: inherit;
	}
	.x6smaf{				/*特殊侧滑栏分割线*/
		border-color:rgba(0,0,0,0.13);
	}
	.T5EAuf .MqFglb, .V5HtW .MqFglb,.yVjhhb .SwqzDd, .IXOIsd .SwqzDd,.MXwBff,.FnVJB,.yVjhhb .F6oKEf, .IXOIsd .F6oKEf,.AjJgof .klaKCc,.yVjhhb .AjJgof, .IXOIsd .AjJgof{				/*特殊侧滑栏分割线*/
		border-color:rgba(0,0,0,0.13);
	}
/* 	酒、饭店预订 */
	.zd2Jbb{				/*整体样式*/
		border-color:rgba(0,0,0,0.13);
		background-color:var(--box-background-color);
		box-shadow: 0 0 30px #eee1 inset,2px 2px 7px rgba(130,170,190,.8);
	}
	.hmHBZd{				/*过渡效果*/
		transition-duration:.5s;
	}
	.hmHBZd:hover{			/*结果选项hover背景*/
		background-color:rgba(43, 135, 240, .55);
	}
/* 	视频结果 */
/* 	g-section-with-header{
		position:fixed;
		z-index:10;
		left:-300px;
	}
	g-section-with-header{
		left:auto;
	}
	g-section-with-header h3{
		text-align:right;
	} */
/* 	谷歌图片结果中的相关搜索 */
	a.scCTtb, a.scCTtb:visited,.lU8dJf{
		border-color:rgba(0,0,0,0.13);
	}
}

