	--helpicon:url(data:image/png;base64,)
}

/********************************专栏文章页面**************************************/
/*专栏文章内容页面*/
.page-container{		/*内容盒子*/
	margin:0 auto;
	padding:20px;
}

/*导航栏*/
.page-container div.nav-tab-bar{			/*左侧导航栏归位*/
	margin-left:-190px;
	position:sticky;
	top: 20px;
	min-height:100vh;
    transform: translateY(-20px);
	background-color:rgba(255,255,255,0.8);
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 10px 1px;
}
.page-container div.nav-tab-bar > a{			/*导航栏选项归位*/
	margin-left:20px;
}

/*右侧作者信息*/
.up-info-holder .fixed-box{			/*盒子位置*/
	position:fixed!important;
	bottom:auto!important;
	padding:20px;
	border-radius:15px;
	background-color:rgba(255, 255, 255, .9);
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 10px 1px;
	transition: background-color 0.3s;
}
.up-info-holder .fixed-box:hover{			/*hover不透明*/
	background-color:#fff;
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 15px 2px;
}
.up-info-holder.to-bottom.scroll .fixed-box{			/*滚动时盒子位置*/
	position:fixed!important;
	top:30px!important;
}
.up-info-holder .fixed-box .more .help .icon{			/*帮助图标*/
	background-image:var(--helpicon);
}
/*可以选中*/
.article-holder.unable-reprint{
	user-select:unset;
}

/********************************浏览历史记录**************************************/
#app .history-wrap{			/*盒子*/
	margin-top:20px;
	padding:20px;
	background-color:rgba(255,255,255,0.8);
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 10px 1px;
	transition: background-color 0.3s;
	border-radius:15px;
}
#app .history-wrap:hover{		/*hover*/
	background-color:#fff;
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 15px 2px;
}
.history-list .r-info{			/*每个记录*/
	background-color:rgba(255,255,255,0.8);
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 10px 1px;
	margin-left:177px;
	margin-bottom:10px;
	padding:10px;
	border-radius:15px;
}
.history-list .r-info:hover{			/*记录hover*/
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 15px 2px;
}
.history-list .r-info .r-txt{			/*去除记录间的分割线*/
	border-color:transparent;
}
.history-list .r-info .history-delete{			/*删除按钮位置*/
	right:1px;
}
.history-list .r-info .lazy-img{			/*预览图片*/
	transition: transform 0.3s;
}
.history-list .r-info:hover .lazy-img{			/*hover图片放大*/
	transform:scale(1.1);
}
.history-wrap .time-label:not(.active){			/*当前时间线以前的时间*/
	background-color:#fff;
}

/********************************广场、专题列表**************************************/
.active-main .active-title .b-active-t,
.topic-main .topic-title .b-topic-t{/*标题文字轮廓*/
	text-shadow:1px 1px 1px #fff,
				1px -1px 1px #fff,
				-1px -1px 1px #fff,
				-1px 1px 1px #fff;
}

/*广场部分*/
.active-main .active-title{/*标题下边框*/
	border-width:0;
}
.active-main .act-list ul li{/*每个item*/
	margin:10px 0;
	padding: 15px;
	border-radius:15px;
	border-width:0;
	background-color:rgba(255,255,255,0.9);
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 10px 1px;
	transition: background-color 0.3s;
}
.active-main .act-list ul li:hover{
	background-color:#fff;
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 15px 2px;
}
@keyframes flash {
    0%, 50%, to {
        opacity:1
    }
    25%, 75% {
        opacity:0
    }
}
.active-main .act-list ul li .event_status .status-ing{/*进行中的活动*/
	color:red;
	animation:flash 2s infinite;
}

/*专题列表部分*/

.topic-main .nav-tab{/*分类栏*/
	border-color:transparent;
	overflow:unset;
	padding:20px;
	border-radius:15px;
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 10px 1px;
	background-color:rgba(255,255,255,.9);
	transition:.3s;
}
.topic-main .nav-tab:after{/*overflow更改导致手动清楚浮动*/
	display:block;
	content:"";
	clear:both;
}

/*分类栏选中优化*/
.topic-main .nav-tab > li:before{
	font-family:'Material Icons';
	content:'\e5c5';
	position:absolute;
	top:-20px;
	left:50%;
	transform:translate(-50%);
	color:#6cf;
	z-index:100;
	transition:.5s ease-in-out;
	opacity:0;
}
.topic-main .nav-tab > li.on:before,
.topic-main .nav-tab > li:hover:before{
	top:-12px;
	opacity:1;
}
.topic-main .nav-tab > li{
	position:relative;
}
.topic-main .nav-tab > li:hover{
	color:#00a1d6;
}
.topic-main .nav-tab > li:after{
	font-family:'Material Icons';
	content:'\e5c7';
	position:absolute;
	bottom:-20px;
	left:50%;
	transform:translate(-50%);
	color:#6cf;
	z-index:100;
	transition:.5s ease-in-out;
	opacity:0;
}
.topic-main .nav-tab > li.on:after,
.topic-main .nav-tab > li:hover:after{
	bottom:-12px;
	opacity:1;
}

/*每个item*/
.topic-main .act-list ul li{
	padding:10px;
	margin:10px 0;
	border-radius:15px;
	background-color:rgba(255,255,255,.9);
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 10px 1px;
	transition:.3s;
}
.topic-main .nav-tab:hover,
.topic-main .act-list ul li:hover{/*hover添加特效*/
	background-color:#fff;
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 15px 2px;
}


/*分页*/
.pagelist{
	margin:20px 0;
}
.pagelist:after{/*清除浮动*/
	display:block;
	content:'';
	clear:both;
}
.pagelist .flip-left > *:not(span){
	background-color:transparent!important;
}
.pagelist .flip-left span{
	margin-bottom:3px;				/*防止box-shadow被超出隐藏*/
	border-radius:20px;
	box-shadow: rgba(0, 0, 0, 0.3) 0 0 3px;
}
.pagelist .flip-left span:not(.prev-page):not(.next-page){			/*去除上一页下一页按钮*/
	height: 40px;
    width: 40px;
	padding:0;
}
.pagelist .flip-right{/*跳转页面*/
    padding: 0 7px 7px;
    border-radius: 30px;
    border: 1px solid #ddd;
    box-shadow: rgba(0, 0, 0, 0.3) 0 0 3px;
	background-color: white;
}

/********************************活动、反馈**************************************/

/*反馈*/
#app > *,
#act-fav-entry ~ #fixed-root ~ #app > *{
	background-color:#fff;
}
#fixed-root ~ #app > *{			/*恢复正常页面的显示*/
	background-color:transparent;
}

#app input,#app textarea{		/*去除表单控件的背景*/
	background-color:transparent;
}
.activity-questions-item{		/*取消问题列表分割的border*/
	border:none!important;
}
.activity-questions-item-title > span:not(.activity-questions-item-required){			/*问题标号*/
	display:inline-block;
	position:relative;
	text-align:center;
	z-index: 100;
    width: 44px !important;
    color: #fff !important;
    background-color: #4879bd;
    border-radius: 12px;
    box-shadow: rgba(0, 0, 0, 0.4) 0 0 3px 0;
	margin-right:5px;
}
.activity-questions-item:hover .activity-questions-item-title > span:not(.activity-questions-item-required){		/*标号hover*/
	animation:questionhover 2s;
}
@keyframes questionhover{		/*动画*/
  0% {
    -webkit-transform-origin: top left;
    transform-origin: top left;
    -webkit-animation-timing-function: ease-in-out;
    animation-timing-function: ease-in-out;
  }
  20%,
  60% {
    -webkit-transform: rotate3d(0, 0, 1, 80deg);
    transform: rotate3d(0, 0, 1, 80deg);
    -webkit-transform-origin: top left;
    transform-origin: top left;
    -webkit-animation-timing-function: ease-in-out;
    animation-timing-function: ease-in-out;
  }
  40%,
  80% {
    -webkit-transform: rotate3d(0, 0, 1, 60deg);
    transform: rotate3d(0, 0, 1, 60deg);
    -webkit-transform-origin: top left;
    transform-origin: top left;
    -webkit-animation-timing-function: ease-in-out;
    animation-timing-function: ease-in-out;
    opacity: 1;
  }
}

/*分享*/
.bili-share-pc{
	box-sizing: border-box;
    height: 78px;
	background-color:rgba(255,255,255,0.8)!important;
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 10px 1px;
	transition:background .2s;
}
.bili-share-pc:hover{
	background-color:#fff!important;
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 15px 2px;
}

/********************************排行榜**************************************/
.bili-footer{				/*footer出现*/
	display:block!important;
}
.main-inner{			/*盒子*/
	margin:20px auto;
	padding:20px;
	background-color:rgba(255,255,255,0.8);
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 10px 1px;
	transition: background-color 0.3s;
	border-radius:15px;
}
.main-inner:hover{		/*hover*/
	background-color:#fff;
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 15px 2px;
}
.rank-item,.rank-list-head .rank_tips,.rank-tab-wrap{				/*盒子内部，包括导航和tips*/
	margin-top:10px;
	border-radius:15px;
	background-color:rgba(255,255,255,0.8);
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 10px 1px;
	transition:.5s;
}
.rank-item:hover {				/*每条排行hover*/
    box-shadow: 0 0px 3px #ccc;
}
.rank-item .content .info{			/*信息右侧留白*/
	padding-right:10px;
}
.rank-item:hover .num{			/*hover数字弹动*/
	animation:numhover 1s;
}

/*动画*/
@keyframes numhover{
  from {
    -webkit-transform: scale3d(1, 1, 1);
    transform: scale3d(1, 1, 1);
  }
  30% {
    -webkit-transform: scale3d(1.25, 0.75, 1);
    transform: scale3d(1.25, 0.75, 1);
  }
  40% {
    -webkit-transform: scale3d(0.75, 1.25, 1);
    transform: scale3d(0.75, 1.25, 1);
  }
  50% {
    -webkit-transform: scale3d(1.15, 0.85, 1);
    transform: scale3d(1.15, 0.85, 1);
  }
  65% {
    -webkit-transform: scale3d(0.95, 1.05, 1);
    transform: scale3d(0.95, 1.05, 1);
  }
  75% {
    -webkit-transform: scale3d(1.05, 0.95, 1);
    transform: scale3d(1.05, 0.95, 1);
  }
  to {
    -webkit-transform: scale3d(1, 1, 1);
    transform: scale3d(1, 1, 1);
  }
}

/********************************游戏赛事**************************************/
/*赛事表*/
.score-contain .allmatch{		/*全部赛事按钮*/
	background-color:#fff;
}
.score-contain .schedule-item .score-wrap{	/*每个比赛*/
	background-color:#fff;
}

/*主体*/
.match-box .box-left > *{			/*左侧所有盒子*/
	margin:0 -10px;
	padding:10px;
	border-radius:0 0 15px 15px;
	background-color:rgba(255,255,255,0.95);
	box-shadow: rgba(0, 0, 0, 0.2) 0px 15px 15px 2px;
	transition:  0.3s;
}
.match-box .box-left > p.main-title,
.match-box .box-left > div > p.main-title{		/*标题去外边距，不撑开盒子*/
	margin-top:0;
	margin-bottom:0;
}
.match-box .box-left > p.main-title,
.match-box .box-left > div[data-id][data-loc-id]{		/*标题盒子*/
	border-radius:15px 15px 0 0;
	background-color:rgba(255,255,255,0.95);
}
.match-box .box-left > p.main-title + div,
.match-box .box-left > div[data-id][data-loc-id] + div{		/*下外边距分割*/
	margin-bottom:20px!important;
}
.match-box .box-left{		/*左侧左移10px*/
	transform:translatex(-10px);
}
.match-box .box-right{		/*右侧*/
	transform:translatex(10px);
	margin:0 -10px;
	padding:10px;
	border-radius:15px;
	background-color:#fff;
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 10px 1px;
	transition: background-color 0.3s;
}
.match-box .box-left > p.main-title:hover + div,
.match-box .box-left > div[data-id][data-loc-id]:hover + div,
.match-box .box-left > *:hover{			/*左侧*/
	background-color:#fff;
	box-shadow: rgba(0, 0, 0, 0.2) 0px 15px 15px 2px;
}
.match-box .box-right:hover{		/*右侧*/
	box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 15px 2px;
}

